import React, { useState } from "react";
import { useNavigate } from "react-router";
import MaskedInput from "react-text-mask";



export default function Create() {
    var lap = 0;
    const laps = [""];
    const [counter,setCounter] = useState(0);
    const [form, setForm] = useState({
        date: "",
        lastName: "",
        track: "",
        laptime: "",
        notes: "",       
        condition: "",
    });
    const navigate = useNavigate();

    const nextLapTime = () => {
        setCounter(counter + 1);
    }

    const removeLapTime = () => {
        if (counter > 0) {
            setCounter(counter - 1);
        } else {
            return counter;
        }    
    }

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    // This function will handle the submission.
    async function onSubmit(e) {
        e.preventDefault();

        // When a post request is sent to the create url, we'll add a new record to the database.
        const newRecord = { ...form };

        await fetch("http://localhost:5000/record/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newRecord),
        })
            .catch(error => {
                window.alert(error);
                return;
            });

        setForm({ date: "", lastName: "", track: "", laptime: "", notes:"", condition: "",});
        navigate("/");
    }

    // This following section will display the form that takes the input from the user.
    return (
        <div>
            <form onSubmit={onSubmit}>
                <div className="form-wrapper">
                    <h3>New Race Entry</h3>
                    <div className="form-group">
                        <label htmlFor="date"><b>Date</b></label>
                        <input
                            type="date"
                            className="form-control"
                            id="date"
                            onChange={(e) => updateForm({ date: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="track"><b>Last Name</b></label>
                        <input
                            type="text"
                            placeholder="Enter last name"
                            className="form-control"
                            id="lastName"
                            value={form.lastName}
                            onChange={(e) => updateForm({ lastName: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="track"><b>Track Name</b></label>
                        <input
                            type="text"
                            placeholder="Enter track name"
                            className="form-control"
                            id="track"
                            value={form.track}
                            onChange={(e) => updateForm({ track: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="laptime"><b>Lap Time</b></label><br></br>
                        <MaskedInput
                            mask={[/[0-9]/, ':', /[0-5]/, /[0-9]/, '.', /[0-9]/, /[0-9]/, /[0-9]/]}
                            className="form-control"
                            placeholder="Enter a lap time: e.g. 1:35.675"
                            guide={true}
                            id="lapMask"
                            onChange={(e) => updateForm({laptime: e.target.value})}/>
                            {Array.from(Array(counter)).map((c, i) => {
                                return <MaskedInput
                                mask={[/[0-9]/, ':', /[0-5]/, /[0-9]/, '.', /[0-9]/, /[0-9]/, /[0-9]/]}
                                className="form-control"
                                placeholder="Enter a lap time: e.g. 1:35.675"
                                guide={true}
                                id="addedLapMask"
                                onChange={(e) => updateForm({lap: e.target.value})}/>
                            })}
                    </div>
                    <button type="button" class="btn btn-secondary" onClick={nextLapTime}>Add lap time</button>
                    <button type="button" class="btn btn-secondary" onClick={removeLapTime}>Remove lap time</button><br></br> 
                    <div className="form-group">
                        <label htmlFor="notes"><b>Notes</b></label>
                        <textarea
                            type="text"
                            placeholder="Optional"
                            className="form-control"
                            id="notes"
                            value={form.notes}
                            onChange={(e) => updateForm({ notes: e.target.value })}
                        />
                    </div>                
                    <div className="form-group" id="dry-wet-buttons">
                        <input id="DryBtn"
                            type="submit"
                            value="Dry"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Dry" })}
                        
                        />
                        <input id="WetBtn"
                            type="submit"
                            value="Wet"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Wet" })}
                        />
                    </div>
                    
                </div>
            </form>
        </div>
    );
}
