import React, { useState, useEffect, Component } from "react";
import { useParams, useNavigate } from "react-router";

export default function Edit() {
    const [form, setForm] = useState({
        track: "",
        laptime: "",
        condition: "",
        records: [],
    });
    const params = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            const id = params.id.toString();
            const response = await fetch(`http://localhost:5000/record/${params.id.toString()}`);

            if (!response.ok) {
                const message = `An error has occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const record = await response.json();
            if (!record) {
                window.alert(`Record with id ${id} not found`);
                navigate("/");
                return;
            }

            setForm(record);
        }

        fetchData();

        return;
    }, [params.id, navigate]);

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    async function onSubmit(e) {
        e.preventDefault();
        const editedRace = {
            track: form.track,
            laptime: form.laptime,
            condition: form.condition,
        };

        // This will send a post request to update the data in the database.
        await fetch(`http://localhost:5000/update/${params.id}`, {
            method: "POST",
            body: JSON.stringify(editedRace),
            headers: {
                'Content-Type': 'application/json'
            },
        });

        navigate("/");
    }

    // This following section will display the form that takes input from the user to update the data.
    return (
        <div className= "ui-1">
            <h3>Update Record</h3>
            <form onSubmit={onSubmit}>
                <div className="track-name">
                <div className="form-group">
                    <label htmlFor="track">Track: </label>
                    <input
                        type="text"
                        placeholder="Enter track name"
                        className="form-control"
                        id="track"
                        value={form.track}
                        onChange={(e) => updateForm({ track: e.target.value })}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="laptime">Laptime: </label>
                    <input
                        type="text"
                        placeholder="Enter time"
                        className="form-control"
                        id="laptime"
                        value={form.laptime}
                        onChange={(e) => updateForm({ laptime: e.target.value })}
                    />
                </div>
                <div className="form-group">
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="conditionOptions"
                            id="conditionDry"
                            value="Dry"
                            checked={form.condition === "Dry"}
                            onChange={(e) => updateForm({ condition: e.target.value })}
                        />
                        <label htmlFor="conditionDry" className="form-check-label">Dry</label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input
                            className="form-check-input"
                            type="radio"
                            name="conditionOptions"
                            id="conditionWet"
                            value="Wet"
                            checked={form.condition === "Wet"}
                            onChange={(e) => updateForm({ condition: e.target.value })}
                        />
                        <label htmlFor="conditionWet" className="form-check-label">Wet</label>
                    </div>
                    </div>
                </div>
                <div>
                    <div className="form-group">
                        <input
                        type="submit"
                        value="Update Record"
                        className="btn btn-primary"
                    />
                    </div>
                </div>
            </form>
        </div>
    );
}
