import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";

export default function Edit() {
    const [form, setForm] = useState({
        lastName: "",
        track: "",
        laptime: "",
        condition: "",
        records: [],
    });
    const params = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            const id = params.id.toString();
            const response = await fetch(`http://localhost:5000/record/${params.id.toString()}`);

            if (!response.ok) {
                const message = `An error has occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const record = await response.json();
            if (!record) {
                window.alert(`Record with id ${id} not found`);
                navigate("/");
                return;
            }

            setForm(record);
        }

        fetchData();

        return;
    }, [params.id, navigate]);

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    async function onSubmit(e) {
        e.preventDefault();
        const editedRace = {
            lastName: form.lastName,
            track: form.track,
            laptime: form.laptime,
            condition: form.condition,
        };

        // This will send a post request to update the data in the database.
        await fetch(`http://localhost:5000/update/${params.id}`, {
            method: "POST",
            body: JSON.stringify(editedRace),
            headers: {
                'Content-Type': 'application/json'
            },
        });

        navigate("/");
    }

    // This following section will display the form that takes input from the user to update the data.
    return (
        <div className= "form-wrapper">
            <h3>Update Record</h3>
            <form onSubmit={onSubmit}>
                <div className="track-name">
                <div className="form-group">
                        <label htmlFor="track">Last Name</label>
                        <input
                            type="text"
                            placeholder="Enter last name"
                            className="form-control"
                            id="lastName"
                            value={form.lastName}
                            onChange={(e) => updateForm({ lastName: e.target.value })}
                        />
                </div>    
                <div className="form-group">
                    <label htmlFor="track">Track: </label>
                    <input
                        type="text"
                        placeholder="Enter track name"
                        className="form-control"
                        id="track"
                        value={form.track}
                        onChange={(e) => updateForm({ track: e.target.value })}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="laptime">Lap Time: </label>
                    <input
                        type="text"
                        placeholder="Enter time"
                        className="form-control"
                        id="laptime"
                        value={form.laptime}
                        onChange={(e) => updateForm({ laptime: e.target.value })}
                    />
                </div>
                <div className="txt-999">
                        <div className="form-group">
                            <input id="DryBtn"
                                type="submit"
                                value="Dry"
                                className="btn btn-primary"
                                onClick={(e) => updateForm({ condition: "Dry" })}
                            />
                            <input id="WetBtn"
                                type="submit"
                                value="Wet"
                                className="btn btn-primary"
                                onClick={(e) => updateForm({ condition: "Wet" })}
                            />
                        </div>
                </div>
                </div>
            </form>
        </div>
    );
}
