import React, { useState } from "react";
import { useNavigate } from "react-router";
import InputMask from "react-input-mask";



export default function Create() {
    const [form, setForm] = useState({
        date: "",
        lastName: "",
        track: "",
        laptime: "",
        notes: "",       
        condition: "",
    });
    const navigate = useNavigate();

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    // This function will handle the submission.
    async function onSubmit(e) {
        e.preventDefault();

        // When a post request is sent to the create url, we'll add a new record to the database.
        const newRecord = { ...form };

        await fetch("http://localhost:5000/record/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newRecord),
        })
            .catch(error => {
                window.alert(error);
                return;
            });

        setForm({ date: "", lastName: "", track: "", laptime: "", notes:"", condition: "",});
        navigate("/Create");
    }

    //Lap time input mask for correct time format - x:xx:xxx(min, sec, ms)
    const lapMins = /[0-9]/;
    const lapSeconds = /[0-6], [0-9]/;
    const lapMs = /[0-9], [0-9], [0-9]/;
    const mask = [lapMins, ":" , lapSeconds, "." , lapMs];

    // This following section will display the form that takes the input from the user.
    return (
        <div>
            <form onSubmit={onSubmit}>
                <div className="form-wrapper">
                    <h3>Create New Lap</h3>
                    <div className="form-group">
                        <label htmlFor="date"><b>Date</b></label>
                        <input
                            type="date" 
                            placeholder=""
                            className="form-control"
                            id="date"
                            value={form.date}
                            onChange={(e) => updateForm({ date: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="track"><b>Last Name</b></label>
                        <input
                            type="text"
                            placeholder="Enter last name"
                            className="form-control"
                            id="lastName"
                            value={form.lastName}
                            onChange={(e) => updateForm({ lastName: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="track"><b>Track Name</b></label>
                        <input
                            type="text"
                            placeholder="Enter track name"
                            className="form-control"
                            id="track"
                            value={form.track}
                            onChange={(e) => updateForm({ track: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="laptime"><b>Lap Time</b></label><br></br>
                        <InputMask mask = "9:99.999" id="lapMask" maskPlaceholder={null} alwaysShowMask onChange={(e) => updateForm({laptime: e.target.value})}/> 
                    </div>
                    <div className="form-group">
                        <label htmlFor="notes"><b>Notes</b></label>
                        <textarea
                            type="text"
                            placeholder="Optional"
                            className="form-control"
                            id="notes"
                            value={form.notes}
                            onChange={(e) => updateForm({ notes: e.target.value })}
                        />
                    </div>                
                    <div className="form-group" id="dry-wet-buttons">
                        <input id="DryBtn"
                            type="submit"
                            value="Dry"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Dry" })}
                        
                        />
                        <input id="WetBtn"
                            type="submit"
                            value="Wet"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Wet" })}
                        />
                    </div>
                    
                </div>
            </form>
        </div>
    );
}
