import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router";
import InputMask from "react-input-mask";
export default function Edit() {
    const [form, setForm] = useState({
        lastName: "",
        track: "",
        laptime: "",
        condition: "",
        records: [],
    });
    const params = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            const id = params.id.toString();
            const response = await fetch(`http://localhost:5000/record/${params.id.toString()}`);

            if (!response.ok) {
                const message = `An error has occurred: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const record = await response.json();
            if (!record) {
                window.alert(`Record with id ${id} not found`);
                navigate("/");
                return;
            }

            setForm(record);
        }

        fetchData();

        return;
    }, [params.id, navigate]);

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    async function onSubmit(e) {
        e.preventDefault();
        const editedRace = {
            lastName: form.lastName,
            track: form.track,
            laptime: form.laptime,
            notes: form.notes,
            condition: form.condition,
        };

        // This will send a post request to update the data in the database.
        await fetch(`http://localhost:5000/update/${params.id}`, {
            method: "POST",
            body: JSON.stringify(editedRace),
            headers: {
                'Content-Type': 'application/json'
            },
        });

        navigate("/");
    }

    // This following section will display the form that takes input from the user to update the data.
    return (
        <div className= "form-wrapper">
            <h3>Edit Lap</h3>
            <form onSubmit={onSubmit}>
                <div className="form-group">
                        <label htmlFor="date"><b>Date</b></label>
                        <input
                            type="date"
                            placeholder=""
                            className="form-control"
                            id="date"
                            value={form.date}
                            onChange={(e) => updateForm({ date: e.target.value })}
                        />
                    </div>
                <div className="form-group">
                        <label htmlFor="track">Last Name</label>
                        <input
                            type="text"
                            placeholder="Enter last name"
                            className="form-control"
                            id="lastName"
                            value={form.lastName}
                            onChange={(e) => updateForm({ lastName: e.target.value })}
                        />
                </div>    
                <div className="form-group">
                    <label htmlFor="track">Track</label>
                    <input
                        type="text"
                        placeholder="Enter track name"
                        className="form-control"
                        id="track"
                        value={form.track}
                        onChange={(e) => updateForm({ track: e.target.value })}
                    />
                </div>
                <div className="form-group">
                        <label htmlFor="laptime">Lap Time</label><br></br>
                        
                        <InputMask value={form.laptime} mask = "9:99.999" id="lapMask" maskPlaceholder={null} alwaysShowMask onChange={(e) => updateForm({laptime: e.target.value})}/> 
                    </div>
                    <div className="form-group">
                        <label htmlFor="notes">Notes</label>
                        <textarea
                            type="text"
                            placeholder="Optional"
                            className="form-control"
                            id="notes"
                            value={form.notes}
                            onChange={(e) => updateForm({ notes: e.target.value })}
                        />
                </div>       
                    <div className="form-group" id="dry-wet-buttons">
                        <input id="DryBtn"
                            type="submit"
                            value="Dry"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Dry" })}
                        />
                        <input id="WetBtn"
                            type="submit"
                            value="Wet"
                            className="btn btn-primary"
                            onClick={(e) => updateForm({ condition: "Wet" })}
                        />
                    </div>
                
            </form>
        </div>
    );
}
