import React, { useState } from "react";
import { useNavigate } from "react-router";

export default function Create() {
    const [form, setForm] = useState({
        track: "",
        laptime: "",
        condition: "",
    });
    const navigate = useNavigate();

    // These methods will update the state properties.
    function updateForm(value) {
        return setForm((prev) => {
            return { ...prev, ...value };
        });
    }

    // This function will handle the submission.
    async function onSubmit(e) {
        e.preventDefault();

        // When a post request is sent to the create url, we'll add a new record to the database.
        const newRecord = { ...form };

        await fetch("http://localhost:5000/record/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newRecord),
        })
            .catch(error => {
                window.alert(error);
                return;
            });

        setForm({ track: "", laptime: "", condition: "" });
        navigate("/");
    }
    // This following section will display the form that takes the input from the user.
    return (
        <div>
            <h3>Create New Record</h3>
            <form onSubmit={onSubmit}>
                <div className="form-wrapper">
                    <div className="form-group">
                        <label htmlFor="track">Track</label>
                        <input
                            type="text"
                            placeholder="Enter track name"
                            className="form-control"
                            id="track"
                            value={form.track}
                            onChange={(e) => updateForm({ track: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="laptime">Laptime</label>
                        <input
                            type="text"
                            placeholder="Enter time"
                            className="form-control"
                            id="laptime"
                            value={form.laptime}
                            onChange={(e) => updateForm({ laptime: e.target.value })}
                        />
                    </div>
                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input
                                className="form-check-input"
                                type="radio"
                                name="conditionOptions"
                                id="conditionDry"
                                value="Dry"
                                checked={form.condition === "Dry"}
                                onChange={(e) => updateForm({ condition: e.target.value })}
                            />
                            <label htmlFor="conditionDry" className="form-check-label">Dry</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input
                                className="form-check-input"
                                type="radio"
                                name="conditionOptions"
                                id="conditionWet"
                                value="Wet"
                                checked={form.condition === "Wet"}
                                onChange={(e) => updateForm({ condition: e.target.value })}
                            />
                            <label htmlFor="conditionWet" className="form-check-label">Wet</label>
                        </div>
                    </div>
                    <div className="txt-999">
                        <div className="form-group">
                            <input
                                type="submit"
                                value="Dry"
                                className="btn btn-primary"
                                onChange={(e) => updateForm({ condition: "Dry" })}

                            />
                        </div>
                    </div>
                </div>
            </form>
        </div>
    );
}
